mapper.insert:
INSERT INTO upload_task (
    file_id, filename, size, chunk_count, chunk_size, sha256, upload_id, state, storage_path, created_at, updated_at
) VALUES (
    #{fileId}, #{filename}, #{size}, #{chunkCount}, #{chunkSize}, #{sha256}, #{uploadId}, #{state}, #{storagePath}, #{createdAt}, #{updatedAt}
)
ON DUPLICATE KEY UPDATE
    filename=VALUES(filename), size=VALUES(size), chunk_count=VALUES(chunk_count), chunk_size=VALUES(chunk_size),
    sha256=VALUES(sha256), upload_id=VALUES(upload_id), state=VALUES(state), storage_path=VALUES(storage_path),
    created_at=VALUES(created_at), updated_at=VALUES(updated_at)

mapper.update:
UPDATE upload_task
SET state = #{state}, updated_at = #{updatedAt}
WHERE file_id = #{fileId}
